import test from 'ava';
import definebabe from '../';

test('DefineBabe::search should return a model', async t => {
  const value = await definebabe.search('emily grey');
  t.same(value, {
    id: 'lns9',
    name: 'Emily Grey',
    slug: 'emily-grey',
    country: 'USA',
    state: 'California',
    rating: 'Good'
  });
});

test('DefineBabe::galleries should return all galleries from a model', async t => {
  const value = await definebabe.galleries('lns9', 'emily-grey');
  t.is(value.length, 44);
});

test('DefineBabe::gallery should return all photos from a gallery', async t => {
  const value = await definebabe.gallery('2qry', 'emily-grey');
  t.is(value.images.length, 34);
});
